import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showTransition, setShowTransition] = useState(false);
  const [showHome, setShowHome] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [events, setEvents] = useState([
    {
      id: 1,
      title: 'Summer Music Festival',
      description: 'A weekend of live music performances',
      longDescription: `Join us for an unforgettable weekend of music and entertainment! 
      The Summer Music Festival brings together top artists from around the world.

      What to expect:
      • Multiple stages with different genres
      • Food and beverage vendors
      • VIP seating options
      • Meet and greet opportunities
      • Exclusive merchandise

      Location: Central Park Amphitheater
      Duration: 2 days
      Capacity: 5000 attendees`,
      date: '2024-07-15',
      price: 1500,
      image: 'https://source.unsplash.com/random/800x600/?concert',
      amenities: ['Parking Available', 'Food Court', 'VIP Lounge', 'First Aid'],
      schedule: [
        { time: '10:00 AM', activity: 'Gates Open' },
        { time: '11:00 AM', activity: 'Opening Act' },
        { time: '2:00 PM', activity: 'Main Performances' },
        { time: '8:00 PM', activity: 'Headline Show' }
      ]
    },
    {
      id: 2,
      title: 'Tech Conference 2024',
      description: 'Learn about the latest technology trends',
      longDescription: `Experience the future of technology at Tech Conference 2024! 
      Network with industry leaders and learn about cutting-edge innovations.

      Conference Highlights:
      • Keynote speakers from top tech companies
      • Interactive workshops
      • Networking sessions
      • Product demonstrations
      • Career fair

      Location: Tech Convention Center
      Duration: 3 days
      Capacity: 2000 attendees`,
      date: '2024-08-20',
      price: 2000,
      image: 'https://source.unsplash.com/random/800x600/?technology',
      amenities: ['Wi-Fi', 'Lunch Included', 'Conference Rooms', 'Tech Support'],
      schedule: [
        { time: '9:00 AM', activity: 'Registration' },
        { time: '10:00 AM', activity: 'Keynote Speech' },
        { time: '12:00 PM', activity: 'Workshop Sessions' },
        { time: '4:00 PM', activity: 'Networking Event' }
      ]
    },
    {
      id: 3,
      title: 'Food & Wine Festival',
      description: 'Taste exotic cuisines and fine wines',
      longDescription: `Indulge in a culinary journey at the Food & Wine Festival! 
      Experience the finest cuisines and wines from around the world.

      Festival Features:
      • International food stations
      • Wine tasting sessions
      • Cooking demonstrations
      • Celebrity chef appearances
      • Gourmet workshops

      Location: Grand Hotel Gardens
      Duration: 1 day
      Capacity: 1000 attendees`,
      date: '2024-09-10',
      price: 1200,
      image: 'https://source.unsplash.com/random/800x600/?food',
      amenities: ['Valet Parking', 'Wine Tasting', 'Cooking Classes', 'Gift Bags'],
      schedule: [
        { time: '11:00 AM', activity: 'Welcome Reception' },
        { time: '12:00 PM', activity: 'Tasting Sessions Begin' },
        { time: '3:00 PM', activity: 'Cooking Demonstrations' },
        { time: '6:00 PM', activity: 'Gala Dinner' }
      ]
    }
  ]);

  useEffect(() => {
    // Simulate fetching events
  }, []);

  const handleLogin = (e) => {
    e.preventDefault();
    // Add your login logic here
    setShowTransition(true);

    // Simulate login delay
    setTimeout(() => {
      setIsLoggedIn(true);
      setTimeout(() => {
        setShowTransition(false);
        setShowHome(true);
      }, 1000);
    }, 500);
  };

  const handleBooking = (eventId) => {
    // Add your booking logic here
  };

  return (
    <div className="App">
      {!isLoggedIn && (
        <div className="auth-container">
          <h2 className="auth-title">Welcome Back</h2>
          <form onSubmit={handleLogin}>
            <div className="form-group">
              <input
                type="email"
                className="form-input"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <input
                type="password"
                className="form-input"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" className="auth-btn">
              Login
            </button>
          </form>
        </div>
      )}

      {showTransition && (
        <div className={`page-transition ${isLoggedIn ? 'exit' : ''}`} />
      )}

      <div className={`home-container ${showHome ? 'visible' : ''}`}>
        {isLoggedIn && (
          <>
            <header className="header">
              <div className="nav-container">
                <h1>Event Management</h1>
                <button 
                  className="auth-btn"
                  onClick={() => {
                    setIsLoggedIn(false);
                    setShowHome(false);
                  }}
                >
                  Logout
                </button>
              </div>
            </header>

            <div className={`events-grid ${showHome ? 'visible' : ''}`}>
              {events.map((event, index) => (
                <div
                  key={event.id}
                  className="event-card"
                  style={{
                    animationDelay: `${index * 0.1}s`
                  }}
                >
                  <div className="event-card-content">
                    <h3>{event.title}</h3>
                    <p>{event.date}</p>
                    <p>{event.longDescription.split('\n')[2].trim()}</p>
                    <button className="book-button" onClick={() => handleBooking(event.id)}>
                      Book Now
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
