import React, { useState } from 'react';
import './App.css';

function App() {
  const [isLoginForm, setIsLoginForm] = useState(true);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [events] = useState([
    {
      id: 1,
      title: 'Summer Music Festival',
      description: 'A weekend of live music performances',
      longDescription: `Join us for an unforgettable weekend of music and entertainment! 
      The Summer Music Festival brings together top artists from around the world.

      What to expect:
      • Multiple stages with different genres
      • Food and beverage vendors
      • VIP seating options
      • Meet and greet opportunities
      • Exclusive merchandise

      Location: Central Park Amphitheater
      Duration: 2 days
      Capacity: 5000 attendees`,
      date: '2024-07-15',
      price: 1500,
      image: 'https://source.unsplash.com/random/800x600/?concert',
      amenities: ['Parking Available', 'Food Court', 'VIP Lounge', 'First Aid'],
      schedule: [
        { time: '10:00 AM', activity: 'Gates Open' },
        { time: '11:00 AM', activity: 'Opening Act' },
        { time: '2:00 PM', activity: 'Main Performances' },
        { time: '8:00 PM', activity: 'Headline Show' }
      ]
    },
    {
      id: 2,
      title: 'Tech Conference 2024',
      description: 'Learn about the latest technology trends',
      longDescription: `Experience the future of technology at Tech Conference 2024! 
      Network with industry leaders and learn about cutting-edge innovations.

      Conference Highlights:
      • Keynote speakers from top tech companies
      • Interactive workshops
      • Networking sessions
      • Product demonstrations
      • Career fair

      Location: Tech Convention Center
      Duration: 3 days
      Capacity: 2000 attendees`,
      date: '2024-08-20',
      price: 2000,
      image: 'https://source.unsplash.com/random/800x600/?technology',
      amenities: ['Wi-Fi', 'Lunch Included', 'Conference Rooms', 'Tech Support'],
      schedule: [
        { time: '9:00 AM', activity: 'Registration' },
        { time: '10:00 AM', activity: 'Keynote Speech' },
        { time: '12:00 PM', activity: 'Workshop Sessions' },
        { time: '4:00 PM', activity: 'Networking Event' }
      ]
    },
    {
      id: 3,
      title: 'Food & Wine Festival',
      description: 'Taste exotic cuisines and fine wines',
      longDescription: `Indulge in a culinary journey at the Food & Wine Festival! 
      Experience the finest cuisines and wines from around the world.

      Festival Features:
      • International food stations
      • Wine tasting sessions
      • Cooking demonstrations
      • Celebrity chef appearances
      • Gourmet workshops

      Location: Grand Hotel Gardens
      Duration: 1 day
      Capacity: 1000 attendees`,
      date: '2024-09-10',
      price: 1200,
      image: 'https://source.unsplash.com/random/800x600/?food',
      amenities: ['Valet Parking', 'Wine Tasting', 'Cooking Classes', 'Gift Bags'],
      schedule: [
        { time: '11:00 AM', activity: 'Welcome Reception' },
        { time: '12:00 PM', activity: 'Tasting Sessions Begin' },
        { time: '3:00 PM', activity: 'Cooking Demonstrations' },
        { time: '6:00 PM', activity: 'Gala Dinner' }
      ]
    }
  ]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const email = e.target.email.value;
      const password = e.target.password.value;

      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
        credentials: 'include'
      });

      const data = await response.json();

      if (response.ok) {
        setUser(data.user);
        setIsLoggedIn(true);
        const authContainer = document.querySelector('.auth-container');
        authContainer.classList.add('slide-out');
      } else {
        alert(data.error || 'Login failed');
      }
    } catch (error) {
      alert('Error logging in. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const username = e.target.username.value;
      const email = e.target.email.value;
      const password = e.target.password.value;

      const response = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, email, password }),
        credentials: 'include'
      });

      const data = await response.json();

      if (response.ok) {
        setUser(data.user);
        setIsLoggedIn(true);
        const authContainer = document.querySelector('.auth-container');
        authContainer.classList.add('slide-out');
      } else {
        alert(data.error || 'Registration failed');
      }
    } catch (error) {
      alert('Error registering. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleLogout = async () => {
    try {
      await fetch('http://localhost:5000/api/auth/logout', {
        method: 'POST',
        credentials: 'include'
      });
      setUser(null);
      setIsLoggedIn(false);
      setSelectedEvent(null);
    } catch (error) {
      alert('Error logging out');
    }
  };

  const handleBookNow = (event) => {
    setSelectedEvent(event);
  };

  const EventModal = ({ event, onClose }) => {
    if (!event) return null;

    return (
      <div className="modal-overlay" onClick={onClose}>
        <div className="modal-content" onClick={e => e.stopPropagation()}>
          <button className="modal-close" onClick={onClose}>&times;</button>
          <div className="modal-header">
            <img src={event.image} alt={event.title} />
            <h2>{event.title}</h2>
          </div>
          <div className="modal-body">
            <div className="event-price">
              <span className="price-tag">₹{event.price}</span>
              <span className="date">📅 {event.date}</span>
            </div>
            <div className="event-description">
              <p>{event.longDescription}</p>
            </div>
            <div className="event-amenities">
              <h3>Amenities</h3>
              <ul>
                {event.amenities.map((amenity, index) => (
                  <li key={index}>{amenity}</li>
                ))}
              </ul>
            </div>
            <div className="event-schedule">
              <h3>Schedule</h3>
              <ul>
                {event.schedule.map((item, index) => (
                  <li key={index}>
                    <span className="time">{item.time}</span>
                    <span className="activity">{item.activity}</span>
                  </li>
                ))}
              </ul>
            </div>
            <button className="proceed-btn" onClick={() => alert('Proceeding to payment...')}>
              Proceed to Payment
            </button>
          </div>
        </div>
      </div>
    );
  };

  const AuthForm = () => (
    <div className={`auth-container ${isLoggedIn ? 'hidden' : ''}`}>
      <div className="auth-box">
        <h2>{isLoginForm ? 'Login' : 'Register'}</h2>
        <form className="auth-form" onSubmit={isLoginForm ? handleLogin : handleRegister}>
          {!isLoginForm && (
            <div className="form-group">
              <label>Username</label>
              <input type="text" name="username" placeholder="Enter username" required />
            </div>
          )}
          <div className="form-group">
            <label>Email</label>
            <input type="email" name="email" placeholder="Enter email" required />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input type="password" name="password" placeholder="Enter password" required />
          </div>
          <button type="submit" className="submit-btn" disabled={isLoading}>
            {isLoading ? 'Loading...' : (isLoginForm ? 'Login' : 'Register')}
          </button>
        </form>
        <p className="toggle-form">
          {isLoginForm ? "Don't have an account? " : "Already have an account? "}
          <button onClick={() => setIsLoginForm(!isLoginForm)}>
            {isLoginForm ? 'Register' : 'Login'}
          </button>
        </p>
      </div>
    </div>
  );

  const EventList = () => (
    <div className={`events-container ${isLoggedIn ? 'slide-in' : ''}`}>
      <div className="user-header">
        {user && <span className="welcome-message">Welcome, {user.username}!</span>}
        <button className="logout-btn" onClick={handleLogout}>Logout</button>
      </div>
      <h2>Upcoming Events</h2>
      <div className="events-grid">
        {events.map(event => (
          <div key={event.id} className="event-card">
            <div className="event-image">
              <img src={event.image} alt={event.title} />
            </div>
            <div className="event-details">
              <h3>{event.title}</h3>
              <p>{event.description}</p>
              <div className="event-info">
                <span className="date">📅 {event.date}</span>
                <span className="price">₹{event.price}</span>
              </div>
              <button className="book-btn" onClick={() => handleBookNow(event)}>
                Book Now
              </button>
            </div>
          </div>
        ))}
      </div>
      {selectedEvent && (
        <EventModal event={selectedEvent} onClose={() => setSelectedEvent(null)} />
      )}
    </div>
  );

  return (
    <div className="App">
      <header className="App-header">
        <h1>Event Management System</h1>
      </header>
      <main>
        <AuthForm />
        <EventList />
      </main>
    </div>
  );
}

export default App;
