import React, { useState } from 'react';
import './App.css';

function App() {
  const [isLoginForm, setIsLoginForm] = useState(true);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [events] = useState([
    {
      id: 1,
      title: 'Summer Music Festival',
      description: 'A weekend of live music performances',
      date: '2024-07-15',
      price: 1500,
      image: 'https://source.unsplash.com/random/800x600/?concert'
    },
    {
      id: 2,
      title: 'Tech Conference 2024',
      description: 'Learn about the latest technology trends',
      date: '2024-08-20',
      price: 2000,
      image: 'https://source.unsplash.com/random/800x600/?technology'
    },
    {
      id: 3,
      title: 'Food & Wine Festival',
      description: 'Taste exotic cuisines and fine wines',
      date: '2024-09-10',
      price: 1200,
      image: 'https://source.unsplash.com/random/800x600/?food'
    }
  ]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const email = e.target.email.value;
      const password = e.target.password.value;

      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
        credentials: 'include'
      });

      const data = await response.json();

      if (response.ok) {
        setUser(data.user);
        setIsLoggedIn(true);
        // Add animation class to auth container
        const authContainer = document.querySelector('.auth-container');
        authContainer.classList.add('slide-out');
      } else {
        alert(data.error || 'Login failed');
      }
    } catch (error) {
      alert('Error logging in. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const username = e.target.username.value;
      const email = e.target.email.value;
      const password = e.target.password.value;

      const response = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, email, password }),
        credentials: 'include'
      });

      const data = await response.json();

      if (response.ok) {
        setUser(data.user);
        setIsLoggedIn(true);
        // Add animation class to auth container
        const authContainer = document.querySelector('.auth-container');
        authContainer.classList.add('slide-out');
      } else {
        alert(data.error || 'Registration failed');
      }
    } catch (error) {
      alert('Error registering. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleLogout = async () => {
    try {
      await fetch('http://localhost:5000/api/auth/logout', {
        method: 'POST',
        credentials: 'include'
      });
      setUser(null);
      setIsLoggedIn(false);
    } catch (error) {
      alert('Error logging out');
    }
  };

  const AuthForm = () => (
    <div className={`auth-container ${isLoggedIn ? 'hidden' : ''}`}>
      <div className="auth-box">
        <h2>{isLoginForm ? 'Login' : 'Register'}</h2>
        <form className="auth-form" onSubmit={isLoginForm ? handleLogin : handleRegister}>
          {!isLoginForm && (
            <div className="form-group">
              <label>Username</label>
              <input type="text" name="username" placeholder="Enter username" required />
            </div>
          )}
          <div className="form-group">
            <label>Email</label>
            <input type="email" name="email" placeholder="Enter email" required />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input type="password" name="password" placeholder="Enter password" required />
          </div>
          <button type="submit" className="submit-btn" disabled={isLoading}>
            {isLoading ? 'Loading...' : (isLoginForm ? 'Login' : 'Register')}
          </button>
        </form>
        <p className="toggle-form">
          {isLoginForm ? "Don't have an account? " : "Already have an account? "}
          <button onClick={() => setIsLoginForm(!isLoginForm)}>
            {isLoginForm ? 'Register' : 'Login'}
          </button>
        </p>
      </div>
    </div>
  );

  const EventList = () => (
    <div className={`events-container ${isLoggedIn ? 'slide-in' : ''}`}>
      <div className="user-header">
        {user && <span className="welcome-message">Welcome, {user.username}!</span>}
        <button className="logout-btn" onClick={handleLogout}>Logout</button>
      </div>
      <h2>Upcoming Events</h2>
      <div className="events-grid">
        {events.map(event => (
          <div key={event.id} className="event-card">
            <div className="event-image">
              <img src={event.image} alt={event.title} />
            </div>
            <div className="event-details">
              <h3>{event.title}</h3>
              <p>{event.description}</p>
              <div className="event-info">
                <span className="date">📅 {event.date}</span>
                <span className="price">₹{event.price}</span>
              </div>
              <button className="book-btn">Book Now</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  return (
    <div className="App">
      <header className="App-header">
        <h1>Event Management System</h1>
      </header>
      <main>
        <AuthForm />
        <EventList />
      </main>
    </div>
  );
}

export default App;
