import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [isVisible, setIsVisible] = useState(false);
  const [username, setUsername] = useState('');
  const [usernameInput, setUsernameInput] = useState('');
  const [password, setPassword] = useState('');
  const [events, setEvents] = useState([
    {
      id: 1,
      title: "Summer Music Festival",
      date: "July 15-17, 2024",
      location: "Central Park Arena",
      description: "Three days of non-stop music featuring top artists from around the world. Experience amazing performances, food stalls, and unforgettable moments.",
      image: "https://images.pexels.com/photos/1105666/pexels-photo-1105666.jpeg",
      price: "$199"
    },
    {
      id: 2,
      title: "Tech Innovation Summit",
      date: "August 5, 2024",
      location: "Digital Convention Center",
      description: "Join industry leaders and innovators for a day of cutting-edge technology discussions, demonstrations, and networking opportunities.",
      image: "https://images.pexels.com/photos/2582937/pexels-photo-2582937.jpeg",
      price: "$299"
    },
    {
      id: 3,
      title: "Food & Wine Festival",
      date: "September 10-12, 2024",
      location: "Riverside Gardens",
      description: "Savor exquisite cuisines and fine wines from renowned chefs and wineries. Includes cooking demonstrations and tasting sessions.",
      image: "https://images.pexels.com/photos/1267696/pexels-photo-1267696.jpeg",
      price: "$150"
    },
    {
      id: 4,
      title: "International Film Festival",
      date: "October 1-7, 2024",
      location: "City Cinema Complex",
      description: "A week-long celebration of cinema featuring premieres, director talks, and screenings of award-winning films from across the globe.",
      image: "https://images.pexels.com/photos/7991579/pexels-photo-7991579.jpeg",
      price: "$180"
    },
    {
      id: 5,
      title: "Wellness & Yoga Retreat",
      date: "November 15-17, 2024",
      location: "Mountain View Resort",
      description: "Rejuvenate your mind and body with expert-led yoga sessions, meditation workshops, and holistic wellness activities.",
      image: "https://images.pexels.com/photos/4056723/pexels-photo-4056723.jpeg",
      price: "$250"
    },
    {
      id: 6,
      title: "New Year's Eve Gala",
      date: "December 31, 2024",
      location: "Grand Ballroom",
      description: "Welcome the new year in style with live entertainment, gourmet dining, and spectacular fireworks display at midnight.",
      image: "https://images.pexels.com/photos/3171837/pexels-photo-3171837.jpeg",
      price: "$399"
    }
  ]);

  useEffect(() => {
    // Simulate fetching events
  }, []);

  const handleLogin = (e) => {
    e.preventDefault();
    setIsLoading(true);
    // Simulate login delay
    setTimeout(() => {
      setIsLoggedIn(true);
      setIsLoading(false);
      setUsername(usernameInput);
      setIsVisible(true);
    }, 1500);
  };

  const handleBooking = (eventId) => {
    // Add your booking logic here
  };

  return (
    <div className="App">
      {!isLoggedIn && (
        <div className={`login-container ${isLoading ? 'loading' : ''}`}>
          <h2 className="auth-title">Welcome Back</h2>
          <form onSubmit={handleLogin}>
            <div className="form-group">
              <input
                type="text"
                className="form-input"
                placeholder="Username"
                value={usernameInput}
                onChange={(e) => setUsernameInput(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <input
                type="password"
                className="form-input"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" className="auth-btn">
              Login
            </button>
          </form>
        </div>
      )}

      {isLoggedIn && (
        <div className={`home-container ${isVisible ? 'visible' : ''}`}>
          <div className="welcome-section">
            <h2>Welcome, {username}! ðŸ‘‹</h2>
            <p>Discover and book amazing events happening near you. We've curated some of the best experiences just for you.</p>
          </div>

          <div className="events-grid">
            {events.map((event, index) => (
              <div
                key={event.id}
                className="event-card"
                style={{
                  animationDelay: `${index * 0.1}s`
                }}
              >
                <div className="event-card-content">
                  <div className="event-image-container">
                    <img src={event.image} alt={event.title} className="event-image" />
                  </div>
                  <div className="event-details">
                    <h3>{event.title}</h3>
                    <div className="event-meta">
                      <i className="far fa-calendar"></i>
                      <span>{event.date}</span>
                    </div>
                    <div className="event-meta">
                      <i className="fas fa-map-marker-alt"></i>
                      <span>{event.location}</span>
                    </div>
                    <p className="event-description">{event.description}</p>
                    <div className="event-footer">
                      <span className="event-price">{event.price}</span>
                      <button className="book-button" onClick={() => handleBooking(event.id)}>
                        Book Now
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
